import React, { useState } from 'react';
import axios from 'axios';
import OpenAI from "openai";

const openai = new OpenAI({
  organization: 'org-LqIa96Wm1L6XKISqUVn5GoxX',
});

const ChatApp = () => {
    const [input, setInput] = useState('');
    const [messages, setMessages] = useState([]);

    const handleInputChange = (event) => {
        setInput(event.target.value);
    };

    // const handleSendClick = async () => {
    //     if (!input.trim()) return;  // Avoid sending empty messages
    //     const message = {
    //         text: input,
    //         from: 'user'
    //     };
    //     setMessages([...messages, message]);  // Display user message in chat
    //     setInput('');  // Clear input field

    //     try {
    //         const response = await axios.post('http://localhost:5000/api/question', { question: input });
    //         const reply = {
    //             text: response.data.answer,
    //             from: 'bot'
    //         };
    //         setMessages(messages => [...messages, reply]);  // Display bot response in chat
    //     } catch (error) {
    //         console.error('Error while fetching data:', error);
    //         setMessages(messages => [...messages, { text: 'Error getting response', from: 'bot' }]);
    //     }
    // };

    const handleSendClick = async () => {
        if (!input.trim()) return;  // Avoid sending empty messages
        const message = {
            text: input,
            from: 'user'
        };
        setMessages([...messages, message]);  // Display user message in chat
        setInput('');  // Clear input field

        try {
            const response = await axios.post('http://localhost:5001/api/question', { question: input });
            const reply = {
                text: response.data.answer,
                from: 'bot'
            };
            setMessages(messages => [...messages, reply]);  // Display bot response in chat
        } catch (error) {
            console.error('Error while fetching data:', error);
            setMessages(messages => [...messages, { text: 'Error getting response', from: 'bot' }]);
        }
    };


    const handleKeyPress = (event) => {
        if (event.key === 'Enter') {
            handleSendClick();
        }
    };

    // return (
    //     <div className="chat-app">
    //         <div className="message-list">
    //             {messages.map((msg, index) => (
    //                 <div key={index} className={`message ${msg.from}`}>
    //                     {msg.text}
    //                 </div>
    //             ))}
    //         </div>
    //         <div className="input-area">
    //             <input
    //                 type="text"
    //                 value={input}
    //                 onChange={handleInputChange}
    //                 onKeyPress={handleKeyPress}
    //                 placeholder="Type your question..."
    //             />
    //             <button onClick={handleSendClick}>Send</button>
    //         </div>
    //     </div>
    // );

    return (
        <div className="chat-app">
            <div className="message-list">
                {messages.map((msg, index) => (
                    <div key={index} className={`message ${msg.from}`}>
                        {msg.text}
                    </div>
                ))}
            </div>
            <div className="input-area">
                <input
                    type="text"
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                    onKeyPress={(e) => e.key === 'Enter' && handleSendClick()}
                    placeholder="Type your question..."
                />
                <button onClick={handleSendClick}>Send</button>
            </div>
        </div>
    );

};

export default ChatApp;
